/// <amd-module name="SbisFile/Attach/Option/Getters/Scanner" />
import ResourceGetter = require("File/Attach/Option/GetterLazy");

const GETTER_LINK = "SBIS3.Plugin/Integration/ResourceGetter/Scanner";
const GETTER_TYPE = "ScannerGetter";

/**
 * Класс конфигурации {@link File/IResourceGetter} для получения сканов,
 * передаваемый в {@link File/ResourceGetter/Attach}
 * @class
 * @name SbisFile/Attach/Option/Getters/Scanner
 * @extends File/Attach/Option/GetterLazy
 * @public
 * @author Заляев А.В.
 */
class Scanner extends ResourceGetter {
    /**
     * @param {*} [options] Параметры вызова конструктора
     * @constructor
     * @see File/IResourceGetter
     */
    constructor (options?: any) {
        super (GETTER_LINK, GETTER_TYPE, options || {});
    }
    /**
     * @description
     * Тип геттера передается в метод choose File/Attach, чтобы указать каким способом(геттером) выбираются файлы
     * <pre>
     *   attach.choose(Scanner.getType()); // Получение сканов
     * </pre>
     * @static
     * @method
     * @returns {String} Тип Getter'a
     */
    static getType() {
        return GETTER_TYPE;
    }
}
export = Scanner;
