/// <amd-module name="SbisFile/Attach/Option/Sources/BL" />

import SourceOption = require("File/Attach/Option/Source");
import LocalFile = require("File/LocalFile");

const LINK = "SbisFile/Source/BL";

/**
 * @typedef {Object} Endpoint
 * @property {String} contract Контракт - определяет доступные операции
 * @property {String} [address] Адрес - указывает место расположения сервиса, к которому будет осуществлено подключение
 */

/**
 * @typedef {Object} Binding
 * @property {String} [create]
 * @property {String} [read]
 * @property {String} [update]
 * @property {String} [query]
 * @property {String} [move]
 * @property {String} [copy]
 * @property {String} [merge]
 * @property {String} [destroy]
 */

/**
 * Класс конфигурации ISource для загрузки на Бизнес-Логику через XHR запросы
 * @class
 * @name SbisFile/Attach/Option/Sources/BL
 * @extends File/Attach/Option/Source
 * @public
 * @author Заляев А.В.
 */
class BL extends SourceOption {
    /**
     * @cfg {Endpoint|String} endpoint Конечная точка, обеспечивающая доступ клиента
     * к функциональным возможностям источника данных.
     */
    /**
     * @cfg {Binding} binding Соответствие методов CRUD+ методам БЛ. Определяет,
     * какой метод объекта БЛ соответствует каждому методу CRUD+.
     */
    /**
     * @param {*} [options] Параметры вызова конструктора
     * @constructor
     * @see File/LocalFileLink
     * @see WS.Data/Source/ISource
     */
    constructor (options?: any) {
        super (LINK, LocalFile, options || {});
    }
}
export = BL;
