/// <amd-module name="SbisFile/Attach/Option/Sources/FileStorage" />

import SourceOption = require("File/Attach/Option/Source");
import LocalFile = require("File/LocalFile");

const LINK = "SbisFile/Source/FileStorage";

/**
 * Класс конфигурации ISource для загрузки в FileStorage
 * Позволяет загружать ресурсы сущности {@link File/LocalFile}
 * @class
 * @name SbisFile/Attach/Option/Sources/FileStorage
 * @extends File/Attach/Option/Source
 * @public
 * @author Заляев А.В
 */
class FileStorage extends SourceOption {
    /**
     * @cfg {String} serviceUrl Адрес сервиса FileStorage
     * По умолчанию: "/cstorage/"
     */
    /**
     * @param {Object} [options] Параметры вызова конструктора
     * @constructor
     * @see File/LocalFile
     * @see WS.Data/Source/ISource
     */
    constructor (options?: any) {
        super (LINK, LocalFile, options || {});
    }
}
export = FileStorage;
