/// <amd-module name="SbisFile/Attach/Option/Sources/SbisDiskDirectory" />

import SourceOption = require("File/Attach/Option/Source");
import Directory = require("File/Directory");

const LINK = "SbisFile/Source/SbisDisk";

/**
 * Класс конфигурации ISource для загрузки на СБИС Диск через Xhr - запрос
 * Позволяет загружать ресурсы сущности {@link File/Directory}
 * @class
 * @name SbisFile/Attach/Option/Sources/SbisDiskDirectory
 * @extends File/Attach/Option/Source
 * @public
 * @author Заляев А.В
 */
class SbisDiskDirectory extends SourceOption {
    /**
     * @cfg {String} catalog Каталог для загрузки на сервис
     *
     * <ul>
     *  <li> mydocs – мои документы </li>
     *  <li> commondocs – общие документы </li>
     *  <li> docs – документы </li>
     *  <li> images – картинки </li>
     *  <li> messages – сообщения </li>
     * </ul>
     * По умолчанию: "temp"
     */
    /**
     * @cfg {String} serviceUrl Адрес сервиса СБИС Диск
     * По умолчанию: "/disk/api/v1/"
     */
    /**
     * @param {Object} [options] Параметры вызова конструктора
     * @constructor
     * @see File/LocalFile
     * @see WS.Data/Source/ISource
     */
    constructor (options?: any) {
        super (LINK, Directory, {
            catalog: "temp",
            ...options
        });
    }
}
export = SbisDiskDirectory;
