/// <amd-module name="SbisFile/Attach/Option/Sources/SbisDiskPlugin" />

import SourceOption = require("File/Attach/Option/Source");
import LocalFileLink = require("File/LocalFileLink");

const LINK = "SBIS3.Plugin/Integration/Source/SBISDisk";

/**
 * Класс конфигурации ISource для загрузки на СБИС Диск через СБИС Плагин
 * Позволяет загружать ресурсы сущности {@link File/LocalFileLink}
 * @class
 * @name SbisFile/Attach/Option/Sources/SbisDiskPlugin
 * @extends File/Attach/Option/Source
 * @public
 * @author Заляев А.В.
 */
class SbisDiskPlugin extends SourceOption {
    /**
     * @cfg {String} catalog Каталог для загрузки на сервис
     *
     * <ul>
     *  <li> mydocs – мои документы </li>
     *  <li> commondocs – общие документы </li>
     *  <li> docs – документы </li>
     *  <li> images – картинки </li>
     *  <li> messages – сообщения </li>
     * </ul>
     * По умолчанию: "temp"
     */
    /**
     * @cfg {String} serviceUrl Адресс сервиса СБИС Диск
     * По умолчанию: "/disk/api/v1/"
     */
    /**
     * @param {Object} [options] Параметры вызова конструктора
     * @constructor
     * @see File/LocalFileLink
     * @see WS.Data/Source/ISource
     */
    constructor (options?: any) {
        super (LINK, LocalFileLink, {
            catalog: "temp",
            ...options
        });
    }
}
export = SbisDiskPlugin;
