define('View/Runner/expressions/attrHelper', function () {

   function isAttr(string) {
      return string.indexOf('attr:') === 0;
   }
   function checkAttr(attrs) {
      for (var key in attrs) {
         if (isAttr(key)) {
            return true;
         }
      }
      return false;
   }

   return {
      isAttr: isAttr,
      checkAttr: checkAttr
   };
});