define('Deprecated/Controls/Accordion/plugins/Collapse-plugin', [
   "Core/core-merge",
   "Deprecated/Controls/Accordion/Accordion",
   "Lib/CollapsingNavigation/CollapsingNavigation",
   "Lib/Marker/Marker"
],
      function (cMerge, Accordion, CollapsingNavigation, Marker) {

         'use strict';

          Accordion.CollapsePlugin = Accordion.extendPlugin(cMerge({
            _initCollapsingNavigation: function () {
               var data = this._options.elements.map(function (value) {
                  return {
                     id: value.id,
                     title: value.title
                  };
               });
               CollapsingNavigation._initCollapsingNavigation.call(this, data);
            },
            _getActiveLabelId: function () {
               return this._activeElement;
            },
            _positioningMarker: function () {
               Marker.moveContainerTo(this.getContainer());
               Marker.positionToElement(this.getLabelById(this._getActiveLabelId()));
            }
         }, CollapsingNavigation, {
            preferSource: true,
            clone: true
         }));
      });