define('Deprecated/Controls/FieldCheckbox/design/DesignPlugin',
   [
   "Deprecated/Controls/FieldCheckbox/FieldCheckbox"
],
   function(FieldCheckbox){

      /**
       * @class FieldCheckbox.DesignPlugin
       * @extends FieldCheckbox
       * @plugin
       */
      FieldCheckbox.DesignPlugin = FieldCheckbox.extendPlugin({
         setIsThirdPosition: function(isThirdPosition) {
            this._options.isThirdPosition = isThirdPosition;
            if(!isThirdPosition && this.getValue() === null) {
               this._changeState();
            }
         }
      });
   });