define('Deprecated/Controls/FieldFormatAbstract/design/DesignPlugin',
   [
   "Deprecated/Controls/FieldFormatAbstract/FieldFormatAbstract"
],
   function(FieldFormatAbstract){
      FieldFormatAbstract.DesignPlugin = FieldFormatAbstract.extendPlugin({
         setMask: function(mask) {
            this._options.mask = mask;
            this.setActive(false);
            this._initInputControl();
         }
      });
   });