define("Deprecated/Controls/FilterFloatArea/FilterFloatArea", ["Lib/Control/FloatArea/FloatArea", 'Core/DependencyResolver', 'css!Deprecated/Controls/FilterFloatArea/FilterFloatArea'], function( FloatArea, DependencyResolver ) {

   "use strict";

   DependencyResolver.register('Deprecated/Controls/FilterFloatArea/FilterFloatArea', [], "Lib/Control/FloatArea/FloatArea");

   /**
    * Всплывающая панель для фильтров, отличается внешним видом
    *
    * @class Deprecated/Controls/FilterFloatArea/FilterFloatArea
    * @extends Lib/Control/FloatArea/FloatArea
    * @author Крайнов Д.О.
    * @control
    * @public
    * @deprecated Используйте класс {@link SBIS3.CONTROLS/Filter/Button}.
    */
   var FilterFloatArea = FloatArea.extend(/** @lends Deprecated/Controls/FilterFloatArea/FilterFloatArea.prototype */{
      $protected: {
         _noStackShadowClassPostfix: 'filter'
      },

      _prepareCollapsedSides: function(){
         this._collapsedSides = {'left': true, 'right': true, 'top': true, 'bottom': false};
      },

      /**
       * Подготавливает блоки области
       * @private
       */
      _prepareArea: function(){
         FilterFloatArea.superclass._prepareArea.apply(this, arguments);

         var side = this._options.controlsSide,
             filterClass = 'ws-float-area-filter-shadow';

         $('<div>', {'class': filterClass + '-stretch'}).appendTo(
            $('<div>', {'class': filterClass + ' ' + filterClass + '-' + side}).appendTo(this._containerShadow));
      },
      //В FilterFloaArea никогда не будет title, а его создание съедает 40px, не даем создавать контейнер для title
      _createTitle: function(){
      }
   });

   return FilterFloatArea;
});
