define('Deprecated/Controls/Label/design/DesignPlugin',
   [
   "Deprecated/Controls/Label/Label"
],
   function(Label){

   /**
    * @class Label.DesignPlugin
    * @extends Label
    * @plugin
    */
   Label.DesignPlugin = Label.extendPlugin({
      genieHandlerAfterCreation: function() {
        var labelPlaceholder = this.getContainer().find('.ws-Label__placeholder');
        labelPlaceholder.attr('data-name', 'labelContent');
        if (this.getContainer().attr('gdi')) {
          labelPlaceholder.addClass('genie-Placeholder');
        }
      }
   });
});