define('Deprecated/Controls/TableView/design/DesignPlugin',
   [
   "Deprecated/Controls/TableView/TableView"
],
   function(TableView){
      
      //endregion

   /**
    * @class TableView.DesignPlugin
    * @extends TableView
    * необходим для того, чтобы при установке опций секции display, изменялось отображение табличного представления
    * @plugin
    */
   TableView.DesignPlugin = TableView.extendPlugin({
      setDisplay: function(data) {
        this._options.display = data;
        this._container.html($(this._dotTplFn(this._options)).html());
        this._columnMap = [];
        this._colgroupCache = undefined;
        this._mapColumns();
        this._initialColumns =  this._options.display.columns;
        this._createContainer();
        this._initDataSource();
        this.refresh(true);
      }
   });
});