define('Deprecated/Controls/TreeView/design/DesignPlugin',
   [
   "Deprecated/Controls/TreeView/TreeView"
],
   function(TreeView){
      
      //endregion

   /**
    * @class TreeView.DesignPlugin
    * @extends TreeView
    * @plugin
    */
   TreeView.DesignPlugin = TreeView.extendPlugin({
      setDisplay: function(data) {
        this._options.display = data;
        this._columnMap = [];
        this._colgroupCache = undefined;
        this._mapColumns();
        this._initialColumns =  this._options.display.columns;
        this._container.html("");
        this._createContainer();
      }
   });
});