define('Deprecated/StraightArgsReader', [
   'Deprecated/ReaderSBIS',
   'Deprecated/SerializatorSBIS',
   'Core/core-instance'
], function(ReaderSBIS, SerializatorSBIS, cInstance) {
   var StraightArgsReader;

   /**
    * Специальный ридер для вызова методов, возвращающих Record с нестандартной сигнатурой (произвольное кол-во аргументов)
    * @class Deprecated/StraightArgsReader
    * @extends Deprecated/ReaderSBIS
    * @author Бегунов А.В.
    * @public
    * @deprecated
    */
   StraightArgsReader = ReaderSBIS.extend(/** @lends Deprecated/StraightArgsReader.prototype */{
      _makeArgsForRead: function(args) {
         for (var key in args) {
            if (args.hasOwnProperty(key)) {
               var
                  value = args[key];
               if (cInstance.instanceOfModule(value, 'Deprecated/Record') || cInstance.instanceOfModule(value, 'Deprecated/RecordSet')) {
                  args[key] = SerializatorSBIS.serialize(value);
               }
               else if (value instanceof Date) {
                  args[key] = value.toSQL(null);
               }
            }
         }
         return args;
      }
   });

   return StraightArgsReader;
});