define('Deprecated/StraightArgsUnifiedReader', [
   'Deprecated/ReaderUnifiedSBIS',
   'Deprecated/SerializatorSBIS',
   'Core/core-instance'
], function(ReaderUnifiedSBIS, SerializatorSBIS, cInstance) {
   var StraightArgsUnifiedReader;

   /**
    * Специальный ридер для вызова списочных методов с нестандартной сигнатурой
    * @class Deprecated/StraightArgsUnifiedReader
    * @extends Deprecated/ReaderUnifiedSBIS
    * @author Бегунов А.В.
    * @public
    * @deprecated
    */
   StraightArgsUnifiedReader = ReaderUnifiedSBIS.extend(/** @lends Deprecated/StraightArgsUnifiedReader.prototype */{
      _makeArgsForQuery: function(args) {
         for (var key in args) {
            if (args.hasOwnProperty(key)) {
               var
                  value = args[key];
               if (cInstance.instanceOfModule(value, 'Deprecated/Record') || cInstance.instanceOfModule(value, 'Deprecated/RecordSet')) {
                  args[key] = SerializatorSBIS.serialize(value);
               }
               else if (value instanceof Date) {
                  args[key] = value.toSQL(null);
               }
            }
         }
         return args;
      }
   });

   return StraightArgsUnifiedReader;
});