define('Deprecated/helpers/parseIdentity', [
   'Core/constants'
], function(
   constants
) {

   /**
    * Производит анализ переданного значения. Если массив, то возвращает строку из элементов массива, разделенных запятой.
    * @param {*} value
    * @returns {*} Если переданное значение - массив, то:
    * <ul>
    *    <li>Если массив состоит из одного элемента то возвращает этот элемент</li>
    *    <li>возвращает строку из элементов массива, разделенных запятой.</li>
    * </ul>
    * Если переданное значение не массив, то оно же и возвращается.
    */
   return function(value) {
      if (value instanceof Array) {
         if (value.length === 1) {
            return value[0];
         }

         return value.join(constants.IDENTITY_SPLITTER);
      }

      return value;
   };
});
