define('Deprecated/res/wsmodules/SuggestShowAllDialog/SuggestShowAllDialog',
   [  'Lib/Control/CompoundControl/CompoundControl',
      'html!Deprecated/res/wsmodules/SuggestShowAllDialog/SuggestShowAllDialog',
      'css!Deprecated/res/wsmodules/SuggestShowAllDialog/SuggestShowAllDialog',
      'Deprecated/Controls/TableView/TableView'
   ], function (CompoundControl, dotTplFn) {

      /**
       * Deprecated/res/wsmodules/SuggestShowAllDialog/SuggestShowAllDialog
       * @extends Lib/Control/CompoundControl/CompoundControl
       */
      var SuggestShowAllDialog = CompoundControl.extend({
         _dotTplFn: dotTplFn,
         $protected: {
            _options: {
               autoWidth: false,
               autoHeight: false,
               width: '700px',
               height: '500px'
            }
         },
         $constructor: function () {
            this._setWindowOptions();
         },
         _setWindowOptions: function(){
            var window = this.getParent();
            window._options.resizable = false;
            window._options.caption = 'Все записи';
         }
      });
      return SuggestShowAllDialog;
   });
