define('Core/HtmlJson/resources/decoratedLinkResolver', function() {
   'use strict';

   return function(json) {
      if (!Array.isArray(json) || json[0] !== 'decoratedlink') {
         return json;
      }
      return ['span',
         {
            'class': 'LinkDecorator__wrap'
         },
         ['a',
            {
               'class': 'LinkDecorator__linkWrap',
               target: '_blank',
               href: json[1].href
            },
            ['img',
               {
                  'class': 'LinkDecorator__image',
                  alt: json[1].href,
                  src: json[1].image
               }
            ]
         ]
      ];
   };
});
