define('Lib/Control/Infobox/Demo/MyInfobox/MyInfobox',
   [
   "Lib/Control/Infobox/Infobox",
   "Lib/Control/CompoundControl/CompoundControl",
   "html!Lib/Control/Infobox/Demo/MyInfobox/MyInfobox",
   "Deprecated/Controls/FieldString/FieldString",
   "Deprecated/Controls/FieldCheckbox/FieldCheckbox"
],
   function(CInfobox, CompoundControl, dotTplFn) {
      var moduleClass = CompoundControl.extend({
         _dotTplFn: dotTplFn,
         init: function() {
            moduleClass.superclass.init.call(this);
            this.getChildControlByName("FieldString 2").subscribe("onValueChange", function(){
               CInfobox.show({
                  control: this.getContainer(), // Устанавливаем DOM-элемент, возле которого будет произведено построение инфобокса
                  message: "<p><span style='color: red;'>Внимание:</span> Текст всплывающей подсказки</p>", // Устанавливаем текст, отображаемый в инфобоксе
                  width: 400, // Устанавливаем ширину всплывающей инфобокса
                  delay: 1000, // Устанавливаем задержку в 1 секунду перед отображением инфобокса
                  hideDelay: 2000 // Устанавливаем скрытие инфобокса через 2 секунды после отображения
               });
            });
         }
      });
      return moduleClass;
   }
);