interface OldDocument extends Document {
    attachEvent?: Function;
    detachEvent?: Function;
}

declare let OldDocument: {
    prototype: OldDocument;
    new(): OldDocument;
};

interface OldWindow extends Window {
    attachEvent?: Function;
    detachEvent?: Function;
}

declare var OldWindow: {
    prototype: OldWindow;
    new(): OldWindow;
};

declare type HashMap<T> = {
    [propName: string]: T
};

// Сломает переходы в IDE по ctrl+click
declare module "json!*" {
    const json: object;
    export = json;
}
declare module "tmpl!*" {
    const tmpl: (...args) => string;
    export = tmpl;
}
declare module "SBIS3.*" {
    const module: {
        new (...args)
        [propName: string]: any
    };
    export = module;
}
declare module "optional!*" {
    const module: {
        new (...args)
        [propName: string]: any
    } | null;
    export = module;
}
