import {FetchResponseType} from "Transport/fetch/responseParser.d";
import {FetchConfig, AbortPromise} from "Transport/fetch.d";

/**
 * Транспорт-обёртка над fetch, отвечающая за разбор ответа от сервера.
 * Так же позволяющая предотвратить запрос
 * @param {Transport/FetchConfig} requestConfig
 * @param {Transport/fetch/ResponseType} responseType
 * @return {Transport/AbortPromise}
 *
 * @function
 * @name Transport/SbisTransport
 * @author Заляев А.В.
 * @public
 */
export type SbisTransport = <T = string>(
    requestConfig: Partial<FetchConfig>,
    responseType: FetchResponseType
) => AbortPromise<T>;
