/// <amd-module name="Data/_chain/Flattened" />
/**
 * Разворачивающее звено цепочки.
 * @class WS.Data/Chain/Flattened
 * @extends WS.Data/Chain/Abstract
 * @public
 * @author Мальцев А.А.
 */

import Abstract from './Abstract';
import FlattenedEnumerator from './FlattenedEnumerator';

export default class Flattened<T> extends Abstract<T> /** @lends WS.Data/Chain/Flattened.prototype */{
   // region Data/_collection/IEnumerable

   getEnumerator(): FlattenedEnumerator<T> {
      return new FlattenedEnumerator(
         this._previous
      );
   }

   // endregion Data/_collection/IEnumerable
}

Flattened.prototype['[Data/_chain/Flattened]'] = true;
