/// <amd-module name="Data/_chain/Reversed" />
/**
 * Реверсивное звено цепочки.
 * @class WS.Data/Chain/Reversed
 * @extends WS.Data/Chain/Abstract
 * @public
 * @author Мальцев А.А.
 */

import Abstract from './Abstract';
import ReversedEnumerator from './ReversedEnumerator';

export default class Reversed<T> extends Abstract<T> /** @lends WS.Data/Chain/Reversed.prototype */{
   // region Data/_collection/IEnumerable

   getEnumerator(): ReversedEnumerator<T> {
      return new ReversedEnumerator(
         this._previous
      );
   }

   // endregion Data/_collection/IEnumerable
}

Reversed.prototype['[Data/_chain/Reversed]'] = true;
