/// <amd-module name="Data/_display/FlagsItem" />
/**
 * Элемент коллекции флагов
 * @class WS.Data/Display/FlagsItem
 * @extends WS.Data/Display/CollectionItem
 * @public
 * @author Мальцев А.А.
 */

import CollectionItem from './CollectionItem';
import {di} from '../util';

export default class FlagsItem extends CollectionItem /** @lends WS.Data/Display/FlagsItem.prototype */{
   isSelected(): boolean {
      return this._$owner.getCollection().get(
         this._$contents, this._$owner.localize
      );
   }

   setSelected(selected: boolean) {
      if (this.isSelected() === selected) {
         return;
      }
      this._$owner.getCollection().set(
         this._$contents, selected, this._$owner.localize
      );
   }
}

FlagsItem.prototype._moduleName = 'Data/display:FlagsItem';
FlagsItem.prototype['[Data/_display/FlagsItem]'] = true;

di.register('Data/display:FlagsItem', FlagsItem);
