/// <amd-module name="Data/_display/Search" />
/**
 * Проекция для режима поиска. Объединяет развернутые узлы в один элемент с "хлебной крошкой" внутри.
 * @class WS.Data/Display/Search
 * @extends WS.Data/Display/Tree
 * @public
 * @author Мальцев А.А.
 */

import Tree from './Tree';
import SearchStrategy from './itemsStrategy/Search';
import {di} from '../util';

export default class Search extends Tree /** @lends WS.Data/Display/Search.prototype */{
   _createComposer() {
      let composer = super._createComposer();
      composer.append(SearchStrategy);

      return composer;
   }
}

Search.prototype._moduleName = 'Data/display:Search';
Search.prototype['[Data/_display/Search]'] = true;

di.register('Data/display:Search', Search);
