/// <amd-module name="Data/_source/DataCrudMixin" />
/**
 * Миксин, совместно с WS.Data/Source/DataMixin дающий возможность обобщить логику вызова CRUD.
 * @mixin WS.Data/Source/DataCrudMixin
 * @public
 * @author Мальцев А.А.
 */

import DataSet from './DataSet';
import {Model} from '../type';

const DataCrudMixin = /** @lends WS.Data/Source/DataCrudMixin.prototype */{
   '[Data/_source/DataCrudMixin]': true,

   _prepareCreateResult(data): Model {
      return this._getModelInstance(data);
   },

   _prepareReadResult(data): Model {
      return this._getModelInstance(data);
   },

   _prepareUpdateResult(data, keys) {
      let idProperty = this.getIdProperty();
      let callback = (record, key) => {
         if (key &&
            idProperty &&
            !record.get(idProperty)
         ) {
            record.set(idProperty, key);
         }
         record.acceptChanges();
      };

      if (data && data['[Data/_collection/IList]']) {
         data.each((record, i) => {
            callback(record, keys ? keys[i] : undefined);
         });
      } else {
         callback(data, keys);
      }
      return keys;
   },

   _prepareQueryResult(data: any): DataSet {
      return this._wrapToDataSet(data);
   }
};

export default DataCrudMixin;
