/// <amd-module name="Data/_type/format/MoneyField" />
/**
 * Формат денежного поля.
 *
 * Создадим поле c типом "Деньги":
 * <pre>
 *    var field = {
 *       name: 'foo',
 *       type: 'money'
 *    };
 * </pre>
 * @class WS.Data/Format/MoneyField
 * @extends WS.Data/Format/RealField
 * @public
 * @author Мальцев А.А.
 */

import RealField from './RealField';

export default class MoneyField extends RealField /** @lends WS.Data/Format/MoneyField.prototype */{
   _$precision: number;

   /**
    * @cfg {Boolean} Большие деньги (значение передается строкой, чтобы избежать погрешностей выполнения операций с плавающей запятой)
    * @name WS.Data/Format/MoneyField#large
    * @see isLarge
    */
   _$large: boolean;

   //region Public methods

   /**
    * Возвращает признак "Большие деньги"
    * @return {Boolean}
    * @see large
    */
   isLarge() {
      return this._$large;
   }

   //endregion Public methods
}

MoneyField.prototype['[Data/_type/format/MoneyField]'] = true;
MoneyField.prototype._moduleName = 'Data/type:format.MoneyField';
MoneyField.prototype._typeName = 'Money';
MoneyField.prototype._$precision = 2;
MoneyField.prototype._$large = false;
