/// <amd-module name="Data/_type/format/UniversalField" />
/**
 * Универсальное поле.
 * @class WS.Data/Format/UniversalField
 * @author Мальцев А.А.
 */

interface IDateTimeMeta {
   withoutTimeZone: boolean
}

interface IDictionaryMeta {
   dictionary: Array<any>
}

interface IRealdMeta {
   precision: number
}

interface IMoneyMeta extends IRealdMeta {
   large: boolean
}

interface IMeta extends IDateTimeMeta, IDictionaryMeta, IMoneyMeta {
}

export default class UniversalField /** @lends WS.Data/Format/UniversalField.prototype */{
   /**
    * Field type
    */
   type: string;

   /**
    * Field name
    */
   name: string;

   /**
    * Default value
    */
   defaultValue: any;

   /**
    * Value can be null
    */
   nullable: boolean;

   /**
    * Metadata
    */
   meta: IMeta;
}

UniversalField.prototype['[Data/_type/format/UniversalField]'] = true;
// @ts-ignore
UniversalField.prototype._moduleName = 'Data/type:format.UniversalField';
UniversalField.prototype.type = '';
UniversalField.prototype.name = '';
UniversalField.prototype.defaultValue = null;
UniversalField.prototype.nullable = false;
UniversalField.prototype.meta = null;
